function isSameDayAndMonth(m1, m2) {
    if (m1 == undefined || m2 == undefined) {
        return false;
    } else {

        m1 = moment(m1);
        m2 = moment(m2);
        // console.log(m1,m2)
        return m1.date() === m2.date() && m1.month() === m2.month()
    }
}
