function playmusic(elm, id) {
    var x = document.getElementById("audio_" + id);
    var y = document.getElementById("msgaudio_" + id);
    // console.log(16623,$(elm));
    if ($(elm).hasClass('all-audios')) {
        if (!$(elm).hasClass('active')) {
            x.play();
            $(elm).addClass('active');
        } else {
            x.pause();
            $(elm).removeClass('active');
        }
    } else {
        if (!$(elm).hasClass('active')) {
            y.play();
            $(elm).addClass('active');
        } else {
            y.pause();
            $(elm).removeClass('active');
        }
    }
}

