function getconvidfromlist(myid, userid) {
    var fulllist = $("#conversation_list_sidebar li");
    for (var i = 0; i < fulllist.length; i++) {
        var ele = $(fulllist[i]);
        if (ele.attr("data-myid") == myid && ele.attr("data-id") == userid) {
            return ele.attr("data-conversationid");
        }
    }
    return -1;
}

