function getNameCommaAnd(data) {
    var returndata = '';
    if (data.length == 1) {
        returndata = data[0];
    } else {
        $.each(data, function(k, v) {
            if (data.length == k + 1) {
                returndata += ' and ' + v;
            } else {
                if (k == (data.length - 2)) {
                    returndata += v;
                } else {
                    returndata += v + ', ';
                }

            }
        })
    }
    // console.log(data,returndata);
    return returndata;

}
